mapper.insert:
insert into commision_record (id, applicant_type, applicant_id, 
  number, order_detail_id, create_time
  )
values (#{id,jdbcType=VARCHAR}, #{applicantType,jdbcType=SMALLINT}, #{applicationId,jdbcType=VARCHAR}, 
  #{number,jdbcType=DOUBLE}, #{orderDetailId,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select id, applicant_type, applicant_id, number, order_detail_id, create_time
from commision_record
where id = #{id,jdbcType=VARCHAR}

  	select * 
	from commision_record 
	where applicant_type = #{applicantType} and applicant_id = #{applicantId}::uuid 


	  		order by #{orderby} 


	  		order by create_time desc 



  		limit #{pageSize} offset (#{pageIndex}-1)*#{pageSize}
