mapper.insert:

insert into goods (id, name, price, market_price,
  status, stock, intro, 
  description, create_time, create_by, 
  update_time, update_by, is_deleted
  )
values (#{id,jdbcType=VARCHAR}::uuid, #{name,jdbcType=VARCHAR}, #{price,jdbcType=DOUBLE}, #{marketPrice,jdbcType=DOUBLE},
  #{status,jdbcType=SMALLINT}, #{stock,jdbcType=INTEGER}, #{intro,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{createBy,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{isDeleted,jdbcType=BIT}
  )

mapper.select:
  	select * from goods 
  	where is_deleted != true

  		and name like CONCAT('%',#{keyword},'%') 



	  		order by #{orderby} 


	  		order by create_time desc 


  	limit #{pageSize} offset (#{pageIndex}-1)*#{pageSize}
