  #{status,jdbcType=SMALLINT}, #{stock,jdbcType=SMALLINT}, #{intro,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{createBy,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{idDeleted,jdbcType=BIT}
  )
