mapper.update:
update order_influence
set order_id = #{orderId,jdbcType=VARCHAR},
  influence_type = #{influenceType,jdbcType=SMALLINT},
  influence_value = #{influenceValue,jdbcType=DOUBLE}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, order_id, influence_type, influence_value
from order_influence
where id = #{id,jdbcType=VARCHAR}
