mapper.insert:
insert into user (id, wechat_id, wechat_unionid
  )
values (#{id,jdbcType=VARCHAR}, #{wechatId,jdbcType=VARCHAR}, #{wechatUnionid,jdbcType=VARCHAR}
  )

mapper.update:
update user
set wechat_id = #{wechatId,jdbcType=VARCHAR},
  wechat_unionid = #{wechatUnionid,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, wechat_id, wechat_unionid
from user
where id = #{id,jdbcType=VARCHAR}
